mapper.select:
SELECT 

FROM test_course a

WHERE a.id = #{id}

SELECT
FROM test_course a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM test_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test_course SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
