mapper.select:
SELECT

FROM
pz_census
where 1=1
AND eat_date = #{eatDate}
AND eat_week = #{eatWeek}
AND restaurant_id = #{restaurantId}
AND restaurant_name like concat('%',#{restaurantName},'%')
AND service_unit_id = #{serviceUnitId}
AND service_unit_name like concat('%',#{serviceUnitName},'%')
AND eat_date >= #{beginDate}
AND eat_date <= #{endDate}


        ORDER BY ${page.orderBy}


        ORDER BY SERVICE_UNIT_ID DESC,EAT_DATE ASC
